import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const categories = [
  { name: 'Gold', icon: 'ðŸ’›', color: 'gold' },
  { name: 'Silver', icon: 'ðŸ¤', color: 'gray-300' },
  { name: 'Rings', icon: 'ðŸ’', color: 'ruby' },
  { name: 'Necklaces', icon: 'ðŸ“¿', color: 'royal' },
];

const HomePage = () => {
  const [rates, setRates] = useState([]);

  useEffect(() => {
    axios.get('/api/metal-rates/current')
      .then(res => setRates(res.data.rates))
      .catch(() => setRates([]));
  }, []);

  return (
    <div className="bg-white">
      {/* Hero Banner */}
      <section className="bg-gradient-to-r from-gold via-white to-royal py-16 text-center">
        <h1 className="text-5xl md:text-6xl font-elegant font-bold text-gold drop-shadow-lg mb-4">Subha Laxmi Jewellery</h1>
        <p className="text-xl md:text-2xl text-ruby font-premium mb-6">Nepal's Luxury Jewellery Destination</p>
        <Link to="/products" className="inline-block bg-gold text-white font-bold px-8 py-3 rounded-full shadow-lg hover:bg-royal transition">Shop Now</Link>
      </section>

      {/* Categories */}
      <section className="py-10 max-w-5xl mx-auto">
        <h2 className="text-3xl font-elegant text-center mb-8 text-royal">Main Categories</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
          {categories.map(cat => (
            <Link key={cat.name} to={`/products?category=${cat.name}`} className={`flex flex-col items-center bg-${cat.color} bg-opacity-10 rounded-xl p-6 shadow hover:scale-105 transition`}>
              <span className="text-5xl mb-2">{cat.icon}</span>
              <span className="text-lg font-premium text-gold">{cat.name}</span>
            </Link>
          ))}
        </div>
      </section>

      {/* Live Metal Rates */}
      <section className="py-10 bg-royal bg-opacity-5">
        <h2 className="text-2xl font-elegant text-center mb-6 text-gold">Live Metal Rates (per Tola)</h2>
        <div className="flex flex-wrap justify-center gap-8">
          {rates.length === 0 ? (
            <span className="text-gray-500">Loading rates...</span>
          ) : rates.map(rate => (
            <div key={rate.metalType} className="bg-white border-2 border-gold rounded-lg px-8 py-4 shadow text-center">
              <div className="text-xl font-bold text-gold">{rate.metalType}</div>
              <div className="text-2xl font-premium text-royal">NPR {rate.ratePerTola.toLocaleString()}</div>
              <div className="text-xs text-gray-500 mt-1">Updated: {new Date(rate.lastUpdated).toLocaleString()}</div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
