import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';
import axios from 'axios';
import ProductManagement from './ProductManagement';
import MetalRatesManagement from './MetalRatesManagement';
import DashboardHome from './DashboardHome';
import OrderManagement from './OrderManagement';
import SalesAnalytics from './SalesAnalytics';

const AdminDashboard = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        window.location.href = '/admin/login';
        return;
      }

      const response = await axios.get('/api/auth/me', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.data.success) {
        setUser(response.data.user);
      } else {
        localStorage.removeItem('adminToken');
        window.location.href = '/admin/login';
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('adminToken');
      window.location.href = '/admin/login';
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    window.location.href = '/admin/login';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-amber-50 via-orange-50 to-yellow-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-amber-600 mx-auto mb-4"></div>
          <p className="text-amber-700 font-semibold">Loading Admin Panel...</p>
        </div>
      </div>
    );
  }

  const navItems = [
    { name: 'Dashboard', path: '/admin/dashboard', icon: 'üìä', color: 'from-blue-500 to-blue-600' },
    { name: 'Products', path: '/admin/dashboard/products', icon: 'üíé', color: 'from-purple-500 to-purple-600' },
    { name: 'Metal Rates', path: '/admin/dashboard/metal-rates', icon: 'üí∞', color: 'from-green-500 to-green-600' },
    { name: 'Orders', path: '/admin/dashboard/orders', icon: 'üì¶', color: 'from-orange-500 to-orange-600' },
    { name: 'Sales Analytics', path: '/admin/dashboard/analytics', icon: 'üìà', color: 'from-red-500 to-red-600' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-amber-50 via-orange-50 to-yellow-50">
      {/* Header */}
      <header className="bg-gradient-to-r from-amber-600 to-orange-600 shadow-2xl border-b-4 border-amber-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-20">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center mr-4 shadow-lg">
                <span className="text-2xl">üíé</span>
              </div>
              <div>
                <h1 className="text-3xl font-bold text-white">
                  Subha Laxmi Jewellery
                </h1>
                <p className="text-amber-100 text-sm font-medium">Admin Control Panel</p>
              </div>
            </div>
            
            <div className="flex items-center space-x-6">
              <div className="text-right">
                <p className="text-white font-semibold">
                  Welcome, {user?.username}
                </p>
                <p className="text-amber-100 text-sm">Administrator</p>
              </div>
              <button
                onClick={handleLogout}
                className="bg-white/20 backdrop-blur-sm text-white px-6 py-2 rounded-lg font-semibold hover:bg-white/30 transition-all duration-300 border border-white/30"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Sidebar */}
        <aside className="w-72 bg-white/80 backdrop-blur-sm shadow-2xl min-h-screen border-r-2 border-amber-200">
          <div className="p-8">
            <h2 className="text-xl font-bold text-gray-800 mb-8 flex items-center">
              <span className="text-2xl mr-3">üéõÔ∏è</span>
              Navigation
            </h2>
            
            {/* Navigation */}
            <nav className="space-y-3">
              {navItems.map((item) => (
                <Link
                  key={item.name}
                  to={item.path}
                  className={`flex items-center space-x-4 px-6 py-4 rounded-xl transition-all duration-300 transform hover:scale-105 ${
                    location.pathname === item.path
                      ? `bg-gradient-to-r ${item.color} text-white shadow-lg`
                      : 'text-gray-700 hover:bg-gradient-to-r hover:from-gray-100 hover:to-gray-200'
                  }`}
                >
                  <span className="text-2xl">{item.icon}</span>
                  <span className="font-semibold text-lg">{item.name}</span>
                </Link>
              ))}
            </nav>

            {/* Quick Stats */}
            <div className="mt-12 p-6 bg-gradient-to-br from-amber-50 to-orange-50 rounded-xl border border-amber-200">
              <h3 className="font-bold text-gray-800 mb-4 flex items-center">
                <span className="text-xl mr-2">‚ö°</span>
                Quick Stats
              </h3>
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Active Products</span>
                  <span className="font-bold text-green-600">24</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Pending Orders</span>
                  <span className="font-bold text-orange-600">8</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Today's Sales</span>
                  <span className="font-bold text-blue-600">‚Çπ45,000</span>
                </div>
              </div>
            </div>
          </div>
        </aside>

        {/* Main content */}
        <main className="flex-1 p-8">
          <div className="flex items-center justify-between mb-8">
            <div>
              <h2 className="text-3xl font-bold text-gray-800">
                {navItems.find(item => item.path === location.pathname)?.name || 'Dashboard'}
              </h2>
              <p className="text-gray-600 mt-1">
                {new Date().toLocaleDateString('en-US', { 
                  weekday: 'long', 
                  year: 'numeric', 
                  month: 'long', 
                  day: 'numeric' 
                })}
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <div className="bg-white/80 backdrop-blur-sm px-4 py-2 rounded-lg border border-amber-200">
                <span className="text-sm text-gray-600">Last updated:</span>
                <span className="text-sm font-semibold text-gray-800 ml-2">
                  {new Date().toLocaleTimeString()}
                </span>
              </div>
            </div>
          </div>

          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-amber-200 p-8">
            <Routes>
              <Route path="/" element={<DashboardHome />} />
              <Route path="/products" element={<ProductManagement />} />
              <Route path="/metal-rates" element={<MetalRatesManagement />} />
              <Route path="/orders" element={<OrderManagement />} />
              <Route path="/analytics" element={<SalesAnalytics />} />
            </Routes>
          </div>
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
