import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('/api/products')
      .then(res => {
        setProducts(res.data.products);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  return (
    <div className="max-w-6xl mx-auto py-10">
      <h1 className="text-4xl font-elegant text-center text-gold mb-8">Our Products</h1>
      {loading ? (
        <div className="text-center text-gray-500">Loading products...</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
          {products.map(product => (
            <Link to={`/products/${product._id}`} key={product._id} className="bg-white border border-gold rounded-xl shadow hover:shadow-lg transition flex flex-col">
              <img src={product.images[0]} alt={product.name} className="h-48 w-full object-cover rounded-t-xl" />
              <div className="p-4 flex-1 flex flex-col justify-between">
                <div>
                  <h2 className="text-xl font-premium text-royal mb-2">{product.name}</h2>
                  <div className="text-sm text-gray-600 mb-1">{product.category} | {product.metalType}</div>
                  <div className="text-sm text-gray-600">Weight: <span className="font-bold">{product.weight}g</span></div>
                  {product.lossPercentage > 0 && (
                    <div className="text-xs text-ruby">Loss: {product.lossPercentage}%</div>
                  )}
                  <div className="text-sm text-gray-600">Making Charge: <span className="font-bold">NPR {product.makingCharge}</span></div>
                </div>
                <div className="mt-4 text-lg font-bold text-gold">NPR {product.price.toLocaleString()}</div>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
