import React from 'react';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('slj_admin_token');
    navigate('/admin/login');
  };

  return (
    <div className="min-h-screen bg-white flex flex-col">
      <nav className="bg-gold text-white flex items-center justify-between px-8 py-4 shadow">
        <div className="font-elegant text-2xl">Admin Dashboard</div>
        <div className="flex gap-4">
          <Link to="products" className="hover:underline">Products</Link>
          <Link to="metal-rates" className="hover:underline">Metal Rates</Link>
          <button onClick={handleLogout} className="ml-4 bg-ruby px-3 py-1 rounded text-white font-bold">Logout</button>
        </div>
      </nav>
      <main className="flex-1 p-8">
        <Routes>
          <Route path="products" element={<div>Product Management (Coming Soon)</div>} />
          <Route path="metal-rates" element={<div>Metal Rates Management (Coming Soon)</div>} />
          <Route path="*" element={<div>Welcome to the Admin Dashboard. Select a section.</div>} />
        </Routes>
      </main>
    </div>
  );
};

export default AdminDashboard;
