import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('/api/auth/login', { username, password });
      localStorage.setItem('slj_admin_token', res.data.token);
      navigate('/admin/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-gold via-white to-royal">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-xl shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-elegant text-gold mb-6 text-center">Admin Login</h2>
        {error && <div className="mb-4 text-ruby text-center">{error}</div>}
        <div className="mb-4">
          <label className="block mb-1 font-premium">Username or Email</label>
          <input type="text" className="w-full border border-gold rounded px-3 py-2" value={username} onChange={e => setUsername(e.target.value)} required />
        </div>
        <div className="mb-6">
          <label className="block mb-1 font-premium">Password</label>
          <input type="password" className="w-full border border-gold rounded px-3 py-2" value={password} onChange={e => setPassword(e.target.value)} required />
        </div>
        <button type="submit" className="w-full bg-gold text-white font-bold py-2 rounded hover:bg-royal transition">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
