import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';
import axios from 'axios';
import ProductManagement from './ProductManagement';
import MetalRatesManagement from './MetalRatesManagement';
import DashboardHome from './DashboardHome';
import OrderManagement from './OrderManagement';
import SalesAnalytics from './SalesAnalytics';

const AdminDashboard = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        window.location.href = '/admin/login';
        return;
      }

      const response = await axios.get('/api/auth/me', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.data.success) {
        setUser(response.data.user);
      } else {
        localStorage.removeItem('adminToken');
        window.location.href = '/admin/login';
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('adminToken');
      window.location.href = '/admin/login';
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    window.location.href = '/admin/login';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gold-50 to-amber-100 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-gold-600"></div>
      </div>
    );
  }

  const navItems = [
    { name: 'Dashboard', path: '/admin/dashboard', icon: 'ðŸ“Š' },
    { name: 'Products', path: '/admin/dashboard/products', icon: 'ðŸ’Ž' },
    { name: 'Metal Rates', path: '/admin/dashboard/metal-rates', icon: 'ðŸ’°' },
    { name: 'Orders', path: '/admin/dashboard/orders', icon: 'ðŸ“¦' },
    { name: 'Sales Analytics', path: '/admin/dashboard/analytics', icon: 'ðŸ“ˆ' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gold-50 to-amber-100">
      {/* Header */}
      <header className="bg-white shadow-luxury border-b border-gold-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <h1 className="text-2xl font-elegant font-bold text-gold-800">
                Subha Laxmi Jewellery
              </h1>
              <span className="ml-4 text-sm text-gray-600 font-premium">Admin Panel</span>
            </div>
            
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-600 font-premium">
                Welcome, {user?.username}
              </span>
              <button
                onClick={handleLogout}
                className="px-4 py-2 text-sm font-premium text-gold-700 bg-gold-50 rounded-lg hover:bg-gold-100 transition-colors"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Sidebar */}
        <aside className="w-64 bg-white shadow-luxury min-h-screen">
          <div className="p-6">
            <h2 className="text-lg font-elegant font-bold text-gold-800 mb-6">Navigation</h2>
            
            {/* Navigation */}
            <nav className="space-y-2">
              {navItems.map((item) => (
                <Link
                  key={item.name}
                  to={item.path}
                  className={`flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors duration-200 ${
                    location.pathname === item.path
                      ? 'bg-gold-600 text-white shadow-gold'
                      : 'text-gold-100 hover:bg-gold-700 hover:text-white'
                  }`}
                >
                  <span className="text-lg">{item.icon}</span>
                  <span className="font-premium">{item.name}</span>
                </Link>
              ))}
            </nav>
          </div>
        </aside>

        {/* Main content */}
        <main className="flex-1 p-8">
          <div className="flex items-center justify-between mb-8">
            <h2 className="text-2xl font-elegant text-gold-800">
              {navItems.find(item => item.path === location.pathname)?.name || 'Dashboard'}
            </h2>
            <div className="text-sm text-gray-600 font-premium">
              {new Date().toLocaleDateString('en-US', { 
                weekday: 'long', 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric' 
              })}
            </div>
          </div>

          <Routes>
            <Route path="/" element={<DashboardHome />} />
            <Route path="/products" element={<ProductManagement />} />
            <Route path="/metal-rates" element={<MetalRatesManagement />} />
            <Route path="/orders" element={<OrderManagement />} />
            <Route path="/analytics" element={<SalesAnalytics />} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
